import React, { Component,Fragment} from 'react';

class TodoList extends Component{


    constructor(prop){
        super(prop);
        this.state = {
            inputV : "Hello",
            test: [1,2,3,4,5,6,7]
        };
    }
    change(e){
        this.setState({
            inputV: e.target.value
            }
        )
    }
    enter(e){
        if ( e.key === 13) {
            const list = [...this.state.test, e.target.value]
            this.setState({
                test:list
            })
        }
    }


    render(){
        return(
            <Fragment>
                <input
                    value={this.state.inputV}
                    onChange={this.change.bind(this)}
                    onKeyUp={this.enter.bind(this)}
                />
                <ul>
                    {this.state.test.map((value, index)=>{
                        return <li key={index}>{value}</li>
                    })}
                </ul>
            </Fragment>
        );
    }
}

export default TodoList;
