import React, { Component,Fragment} from 'react';

class TodoList extends Component{


    constructor(prop){
        super(prop);
        this.state = {
            inputV : "Hello",
            test: []
        };
    }
    change(e){
        this.setState({
            inputV: e.target.value
            }
        )
    }
    enter(e){
        if ( e.keyCode === 13) {
            const test = [...this.state.test, this.state.inputV]
            this.setState({
                test,
                inputV:""
            })
        }
    }


    render(){
        return(
            <Fragment>
                <input
                    value={this.state.inputV}
                    onChange={this.change.bind(this)}
                    onKeyUp={this.enter.bind(this)}
                />
                <ul>
                    {this.state.test.map((value, index)=>{
                        return <li key={index}>{value}</li>
                    })}
                </ul>
            </Fragment>
        );
    }
}

export default TodoList;
