import React, {Component} from 'react';
import { Button } from 'antd';
import axios from 'axios';
import './style.css';

class Login extends Component{

    constructor(props){
        super(props)
        this.state = {
            login: false
        }
    }



    render(){
        return(
            <div className='button'>
                {this.state.login ?  <Button type="primary">退出</Button> : <Button type="primary">登陆</Button>}
            </div>
        )
    }

    componentDidMount() {
        axios.get('http://www.dell-lee.com/react/api/isLogin.json').then(
            res=>{
                this.setState(res.data.data)
            }
        )
    }
}

export default Login;
