import React, { Component, Fragment} from 'react';
import logo from './logo.png';
import './style.css'
import { Menu, Icon } from 'antd';

class AppHeader extends Component {
    constructor(prop){
        super(prop)
        this.state = {
            list:[
                {
                    key:1,
                    icon:'mail',
                    content:'VOA慢速英语'
                },
                {
                    key:2,
                    icon:'mail',
                    content:'VOA慢速英语'
                },
                {
                    key:3,
                    icon:'mail',
                    content:'VOA慢速英语'
                },
                {
                    key:4,
                    icon:'mail',
                    content:'VOA慢速英语'
                },
                {
                    key:5,
                    icon:'mail',
                    content:'VOA慢速英语'
                },
                {
                    key:6,
                    icon:'mail',
                    content:'VOA慢速英语'
                }
            ]
        }
    }
    getMenuitem(){
        return(
            this.state.list.map((item)=>{
                return (
                    <Menu.Item key={item.key}>
                        <Icon type={item.icon} />{item.content}
                    </Menu.Item>
                )

            })
        )
    }


    render() {
        return (
            <Fragment>
                <img className='app-header-logo' src={logo}/>
                <Menu mode="horizontal" className="app-header-menu">
                    {this.getMenuitem()}
                </Menu>
            </Fragment>
        )

    }
}

export default AppHeader;
